import React, { useState } from 'react';


type transactionType = {
    transction_id: number,
    customer_id: string,
    total_price: number,
    currency: string,
    cerdit_card_type: string,
    cerdit_card_number: string
}


const EditTransaction = ({ transaction: transactionType }) => {

    const [tarns, setTrans] = useState<transactionType>(transaction)
    const [customer, setCustomer] = useState<string>(tarns.customer_id)
    const [price, setPrice] = useState<number>(tarns.total_price)
    const [currency, setCurrency] = useState<string>(tarns.currency)
    const [ccType, setCcType] = useState<string>(tarns.cerdit_card_type)
    const [ccNumber, setCcNumber] = useState<string>(tarns.cerdit_card_number)


    const updateTransaction = async () => {
        try {
            const body: transactionType = {
                transction_id: props.transaction.transction_id,
                customer_id: customer,
                total_price: price,
                currency: currency,
                cerdit_card_type: ccType,
                cerdit_card_number: ccNumber
            }

            const response = await fetch(`http://localhost:3001/transactions/${transaction.transction_id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            });

        } catch (err) {
            console.error(err)
        }
    }


    return (
        <>

            <button type="button" className="btn btn-primary" data-toggle="modal" data-target={`#id${transaction.transction_id}`}>
                Edit
</button>

            <div className="modal" id={`id${transaction.transction_id}`} onClick={() => setTrans(transaction)}>
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h4 className="modal-title">Edit Transaction</h4>
                            <button type="button" className="close" data-dismiss="modal" onClick={() => setTrans(transaction)} >&times;</button>
                        </div>

                        <div className="modal-body">
                            <input
                                type="text"
                                value={customer}
                                onChange={e => setCustomer(e.target.value)}
                            />
                            <input
                                type="number"
                                value={price}
                                onChange={e => setPrice(parseFloat(e.target.value))}
                            />
                            <input
                                type="text"
                                value={currency}
                                onChange={e => setCurrency(e.target.value)}
                            />
                            <input
                                type="text"
                                value={ccType}
                                onChange={e => setCcType(e.target.value)}
                            />
                            <input
                                type="text"
                                value={ccNumber}
                                onChange={e => setCcNumber(e.target.value)}
                            />
                        </div>

                        <div className="modal-footer">
                            <button type="button" className="btn btn-primary" data-dismiss="modal" onClick={e => updateTransaction()} >Edit</button>
                            <button type="button" className="btn btn-danger" data-dismiss="modal" onClick={() => setTrans(transaction)}>Close</button>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default EditTransaction;
